green='\e[0;32m'; red='\e[1;31m'; endColour='\e[0m'; for x in c++/*.cc; do x=$(echo $x|cut -c 5- |rev| cut -c 4- |rev); make c++ $x > /dev/null; output=$(./c++/$x); answer=$(eval "sed -n '"$x"p' assets/answers.txt"); if [ $output == $answer ]; then echo -e "${green}Test $x passed${endColour}"; else echo "${red}Test $x failed${endColour}"; fi; done